package fr.petitl.relational.pg.gis.model;

import fr.petitl.relational.repository.annotation.PK;
import fr.petitl.relational.repository.annotation.Table;
import org.postgis.MultiPolygon;
import org.postgis.Point;

/**
 * Created by loic on 12/06/15.
 */
@Table("Location")
public class Location {
    public static final String DROP = "DROP TABLE Location;";
    public static final String CREATE = "CREATE TABLE Location (id TEXT, label TEXT, geometry GEOMETRY(MULTIPOLYGON, 4326), reference GEOMETRY(POINT, 4326))";
    public static final String INSERT = "INSERT INTO Location VALUES ('75056', 'Paris', '', '0101000020E61000004B6324100DBD0240D2DE3897A66D4840')";;

    @PK
    private String id;

    private String label;

    private MultiPolygon geometry;

    private Point reference;

    public String getId() {
        return id;
    }

    public void setId(String id) {
        this.id = id;
    }

    public String getLabel() {
        return label;
    }

    public void setLabel(String label) {
        this.label = label;
    }

    public MultiPolygon getGeometry() {
        return geometry;
    }

    public void setGeometry(MultiPolygon geometry) {
        this.geometry = geometry;
    }

    public Point getReference() {
        return reference;
    }

    public void setReference(Point reference) {
        this.reference = reference;
    }
}
